const showDialogMethod = () => {
   dialog.showConfirmDialog({
     title: 'title',
     message: 'Asynchronous shutdown example',
     beforeClose
   })
}
const beforeClose = (action: any) =>
   new Promise((resolve) => {
     setTimeout(() => {
       // action !== 'confirm' intercepts the cancellation operation
       resolve(action === 'confirm')
     }, 1000)
   })
