import { useToast } from '../../uni_modules/zebra-ui'
const toast = useToast()

const toastData = toast.showLoadingToast({
  duration: 0,
  forbidClick: true,
  message: '倒计时 3 秒',
});

let second = 3;
const timer = setInterval(() => {
  second--;
  if (second) {
    toastData!.message.value = `倒计时 ${second} 秒`
  } else {
    clearInterval(timer);
    toast.closeToast();
  }
}, 1000);

