import { ref } from 'vue'
import { onPageScroll } from '@dcloudio/uni-app'
const list = ref([
   {
     items: [] as string[],
     refreshing: false,
     loading: false,
     error: false,
     finished: false
   },
   {
     items: [] as string[],
     refreshing: false,
