import { ref } from 'vue';

export default {
   setup() {
     const date = ref('');
     const show = ref(false);

     const formatDate = (date) => `${date.getMonth() + 1}/${date.getDate()}`;
     const onConfirm = (values) => {
       const [start, end] = values;
       show.value = false;
       date.value = `${formatDate(start)} - ${formatDate(end)}`;
     };

     return {
       date,
       show,
       onConfirm,
     };
   },
};

