import { ref } from 'vue'
const cover = ref(
  'https://cdn.zebraui.com/zebra-ui/images/swipe-demo/swipe1.jpg'
)

import { useDialog } from '../../uni_modules/zebra-ui'
const dialog = useDialog()
const beforeClose = ({ position }: { position: string }) => {
  switch (position) {
    case 'left':
    case 'cell':
    case 'outside':
      return true
    case 'right':
      return new Promise<boolean>((resolve) => {
        dialog
          .showConfirmDialog({
            title: '确定删除吗'
          })
          .then(() => resolve(true))
          .catch(() => resolve(false))
      })
  }
}
